function() {
  var clickURL = {{Click URL}};
  var cleanURL = clickURL.replace(/\&?_ga=([^&]*)/gi,'');   // ga linker
  cleanURL = cleanURL.replace(/\&?_gac=[^&]*/gi,'');        // campaign information
  cleanURL = cleanURL.replace(/\&?li_fat_id=[^&]*/gi,'');   // linkedin
  cleanURL = cleanURL.replace(/\&?fbclid=[^&]*/gi,'');      // facebook
  cleanURL = cleanURL.replace(/\&?msclkid=[^&]*/gi,'');     // bing
  cleanURL = cleanURL.replace(/\&?zanpid=[^&]*/gi,'');      // awin (zanox)
  cleanURL = cleanURL.replace(/\&?_gl=[^&]*/gi,'');         // google linker
  cleanURL = cleanURL.replace(/\?\&/gi,'?');                // clean-up &?
  cleanURL = cleanURL.replace(/\?$/gi,'');                  // clean-up trailing ?
  return cleanURL;
}

